    System.out.println("Please enter your age: ");
    int age = input.nextInt(); 

    /***********PROCESS**************/
    //Calculate year of birth
    int discount = 0;

    if (age < 18) {
      discount = 30;
    }
    else if (age >= 18 && age <= 30) {
      discount = 45;
    }
