public class BatteryCharging {

    double[] chargingTime;

    public BatteryCharging(int count)  {chargingTime = new double[count];}

    public void set(int index, double value ){ chargingTime[index] = value;}
    public int size(){ return chargingTime.length;}
    public double get(int index){ return chargingTime[index];} 

        public String listAllBatteryChargeTimes(){
        String values = "[ ";
        for(int i = 0; i < size();i++){
            if(i< size()-1){values += get(i) + " , ";}
            else{values += get(i) ;}
        } 
        values += " ]";
        return values;
    }

    public double shortestCharge(){
        double value = chargingTime[0];
        for(int i = 1; i < size();i++){
            if(value > get(i)){ value = get(i); }
        }
        return value;
    }

    public double longestCharge(){
        double value = chargingTime[0];
        for(int i = 1; i < size();i++){
            if(value < get(i)){ value = get(i); }
        }
        return value;
    }  

    public void replace(int index, double value){        
     chargingTime[index] = value;

    }       
}
